package name.yumao.douyu.swing;

import java.awt.Color;
import java.awt.Toolkit;
import java.awt.event.ActionEvent;
import java.awt.event.ActionListener;
import java.io.BufferedInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.InputStream;
import java.text.SimpleDateFormat;
import java.util.Calendar;
import java.util.Properties;
import java.util.TimeZone;

import javax.swing.JFrame;
import javax.swing.JLabel;
import javax.swing.JScrollPane;
import javax.swing.JTable;

import name.yumao.douyu.http.HttpClientFromDouyu;
import name.yumao.douyu.utils.HexUtils;
import name.yumao.douyu.utils.SQLiteReadBDS;
import name.yumao.douyu.utils.SQLiteUtils;
import name.yumao.douyu.utils.SQLiteWriteBDS;

public class Donate extends JFrame implements ActionListener{
	private static final long serialVersionUID = 1L;
	//JLabel supLabel=new JLabel(HttpClientFromDouyu.showAnnounce());
	JTable jTable = new JTable();
	
	public Donate(String inNum){
		super("斗鱼TV鱼丸英雄榜");
		Properties properties = null;
		try {
			InputStream inputStream=new BufferedInputStream(new FileInputStream("conf"+File.separator+"donate.properties"));
			properties = new Properties();
			properties.load(inputStream);
		} catch (Exception e1) {
		}
		if(Boolean.parseBoolean(properties.getProperty("DonateList_Switch"))){
			Color _messageColor = new Color(Integer.parseInt(properties.getProperty("DonateList_font_color_RGB").substring(1,3),16),Integer.parseInt(properties.getProperty("DonateList_font_color_RGB").substring(3,5),16) ,Integer.parseInt(properties.getProperty("DonateList_font_color_RGB").substring(5,7),16));
			Toolkit toolkit = Toolkit.getDefaultToolkit();
			setIconImage(toolkit.createImage(HexUtils.HexString2Bytes("")));
			setBounds(500, 200, 320, 320);
			setVisible(true);
			setResizable(false); 
			setLayout(null);
			setAlwaysOnTop(Boolean.parseBoolean(properties.getProperty("DonateList_AlwaysTop")));
			setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
			Calendar calendar = Calendar.getInstance(TimeZone.getTimeZone("GMT+08:00"));
			SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd");
			String dabPath = "logs"+File.separator+"dab"+File.separator+simpleDateFormat.format(calendar.getTime())+"-"+inNum+".dab";
			SQLiteReadBDS.setSqliteUrl(dabPath);
			SQLiteWriteBDS.setSqliteUrl(dabPath);
			SQLiteUtils.initDB();
			JScrollPane jScrollPane = new JScrollPane();
			jScrollPane.setBounds(10, 10, 290, 270);
			jScrollPane.setBackground(new Color(0,0,0,0));
			add(jScrollPane);
			while(true){
				jTable = SQLiteUtils.queryDonateList(jTable );
				jTable.setForeground(_messageColor);
				jTable.setBorder(null);
				jScrollPane.setViewportView(jTable);
				try {
					remove(jTable);
					Thread.sleep(1000);
				} catch (InterruptedException e) {
					e.printStackTrace();
				}
			}
		}
	}
	@Override
	public void actionPerformed(ActionEvent e) {
	}
}
